#Nombre d'éléments dans le tableau
def nbHandle(j)
  puts "Il y a #{j.count} journalistes"
  return
end
nbHandle(journalists)

#Les plus courts
a=journalists.min(2) {|a, b| a.length <=> b.length}
puts a

#Combien y-a-t'il de handle contenant 5 caractères (le @ ne compte pas pour un caractère)
countSize = 0
journalists.each do |sizeString|
  if
    sizeString.size == 6
    countSize += 1
  end
end
puts ("Il y a #{countSize} élements avec une taille de 5 caractères sans le @.")

#Combien commencent par une majuscule (première lettre juste après le @) ?
i = 0
journalists.each do |majoumin|
  noat = majoumin.delete "@"+"_"
  first_letter = noat.chr

  if first_letter.upcase == first_letter
    i += 1
  end
end
puts "Il y a #{i} handles qui commencent par une majuscule."

#Trie la liste de handle par ordre alphabétique.
journalists_cp = journalists

journalists_cp.each do |journalists_downcase|
  journalists_downcase.downcase!
end
journalists_cp.sort! {|first, second| first <=> second}
puts journalists_cp

#Trie la liste de handle par taille des handle (les plus petits en premiers, les plus grands après)
puts "Les handles triés par ordre de grandeur : "
liste_ordre_longueur = journalists.sort_by(&:length)
puts liste_ordre_longueur


#Quelle est la position dans l'array de la personne @epenser ?
wherIsEpenser = journalists.index("@epenser")
puts "@epenser est à la place #{wherIsEpenser + 1}"

#Sors-moi une répartition des handle par taille de ces derniers (nombre de handle avec 1 caractère, nombre de handle avec 2 caractères, nombre de handle avec 3 caractères, etc)

size_array=[]
for item in journalists
  l=item.length
  size_array |= [l]
end
puts size_array
handle_size=[]
for item in journalists
  handle_size << item.length

end
for item in size_array
  puts "Il y a : #{handle_size.count(item)} handle de #{item} caractères"
end